class Search extends React.Component {

   getPOkemonFromApi = async (id) => {
    let url ='https://pokeapi.co/api/v2/type/'+id
    let response = await axios.get(url)
    console.log('--getPOkemonFromApi--')
    console.log(url)
    console.log(response.data)
    console.log('--fin getPOkemonFromApi--')
    return response.data
  }

  render() {
    console.log('RENDER')
    return (
      <View style={styles.main_container}>
        <Text>

        </Text>

        {/* <CityDescription city={this.state.city} ephemeride={this.state.ephemeride} /> */}
        {/* forecast={this.state.forecast} */}

        <FlatList
          data={this.state.city}
          keyExtractor={(item) => item.insee}
          renderItem={({ item }) => <CityDescription city={item} />}
          />
      { this._displayLoading() }
      </View>

    )
  }
  constructor(props) {
    super(props)
    this.state = {
      cities: [],
      isLoading: false // Par défaut à false car il n'y a pas de chargement tant qu'on ne lance pas de recherche
    }

  }

  // Nouvelle méthode
  _searchTextInputChanged(text) {
    this.searchedText = text // Modification du texte recherché à chaque saisie de texte, sans passer par setState
  }

 // Bien noter les deux setState
 //   isLoading: True puis appel API puis lorsque l'API a répondu isLoading: False
 _loadCities() {
    if (this.state.isLoading) return
    if (this.searchedText.length > 0) {
      this.setState({ isLoading: true })
      getPOkemonFromApi(this.searchedText).then((data) => {
        this.setState({ city: data.city, isLoading: false });
        this.setState({ ephemeride : data.ephemeride , isLoading: false });
        // this.setState({ forecast : data.forecast , isLoading: false });
        console.log(JSON.stringify(data,undefined,true))

      });
    }
  }

    _displayLoading() {
      if (this.state.isLoading) {
        return (
          <View style={styles.loading_container}>
            <ActivityIndicator size='large' />
          {/* Le component ActivityIndicator possède une propriété size pour définir la taille du visuel de chargement : small ou large. Par défaut size vaut small, on met donc large pour que le chargement soit bien visible */}
          </View>
      )
    }
  }
}
export default Search


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
}
)
